    def _make_visit_admonition(name):
        # type: (unicode) -> Callable[[TexinfoTranslator, nodes.Node], None]
        def visit(self, node):
            # type: (nodes.Node) -> None
            self.visit_admonition(node, admonitionlabels[name])
        return visit

